import CartIcon from '../Cart/CartIcon';
import classes from './HeaderCartButton.module.css';
import CartContext from '../../store/CartContext';
import { useContext, useEffect } from 'react';

const HeaderCartButton = props => {

    const cartCtx = useContext(CartContext);

    const numberOfItems = cartCtx.items.reduce((numOfItems, item) => {
        return numOfItems + item.amount;
    }, 0)

    console.log('headerCartBttuon rendered');

    useEffect(() => { console.log(classes.button) }, [numberOfItems]);

    return <button className={classes.button} onClick={props.openCartHandler}>
        <span className={classes.icon}>
            <CartIcon />
        </span>
        <span>Your Cart</span>
        <span className={classes.badge}>{numberOfItems}</span>
    </button>
}


export default HeaderCartButton;
